public InputStream openDisplayPhoto(String photoUri) {
    try {
        AssetFileDescriptor fd = getContentResolver().openAssetFileDescriptor(photoUri, "r");
        return fd.createInputStream();
    } catch (IOException e) {
        return null;
    }
}
